sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	//If the new value is empty then do nothing
    if (newValue == '') {
       return;
    }

	var wnName = 'u_what_needed';
	var whatNeeded = g_form.getValue(wnName);	

	//Clear old options in order to set up new ones
	g_form.clearOptions(wnName);

	//Add Choice options based on the new value selected in 'Request Type' field
	switch(newValue){
		case 'hr':
			g_form.addOption(wnName, 'hr1', 'Human Resources 1');
			g_form.addOption(wnName, 'hr2', 'Human Resources 2');
			break;
		case 'facilities':
			g_form.addOption(wnName, 'facilities1', 'Facilities 1');
			g_form.addOption(wnName, 'facilities2', 'Facilities 2');
			break;
		case 'legal':
			g_form.addOption(wnName, 'legal1', 'Legal 1');
			g_form.addOption(wnName, 'legal2', 'Legal 2');
			break;
	}
	//Always add an 'Other' option
	g_form.addOption(wnName, 'other', 'Other');

	//If the form is loading and its not a new record then set 'What needed' to its value before it was loaded
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue(wnName, whatNeeded);
	}
}
