sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
		var dtToday = new GlideDateTime();
		var dtWhenNeeded = new GlideDateTime(strDate);
		var dToday = dtToday.getLocalDate();
		var dWhenNeeded = dtWhenNeeded.getLocalDate();

		return dWhenNeeded.before(dToday);
	},
	isDateToday: function(strDate){
		var dtToday = new GlideDateTime();
		var dtWhenNeeded = new GlideDateTime(strDate);
		var dToday = dtToday.getLocalDate();
		var dWhenNeeded = dtWhenNeeded.getLocalDate();

		return dToday.compareTo(dWhenNeeded) == 0;
	},
    type: 'NeedItUtils'
};
